class Paginas_model extends MY_Model {
	/**
	 * Construtor que inicializa a classe pai MY_Model
	 * e configura o nome da tabela principal e das colunas da tabela.
	 *
	 * @return void
	 */
	public function __construct ()
	{
		parent::__construct();
		$this->titulo = 'Páginas';
		$this->tabela = 'paginas';
		$this->colunas = '`id`, `dt_registro` as dt_tmp,`titulo`,`slug`,`ativo`,`conteudo`,DATE_FORMAT(`dt_registro`, "%d/%m/%Y") as `dt_registro`';
	}
	/**
	 * cria a tabela dos paginas
	 *
	 * @return integer
	 */
	public function cria_tabela()
	{
		$sql = 'CREATE TABLE IF NOT EXISTS `'.$this->tabela.'` (
		  `id` int(11) NOT NULL AUTO_INCREMENT,
		  `dt_registro` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
		  `titulo` varchar(100) NOT NULL,
		  `slug` varchar(255) NULL,
		  `conteudo` text NOT NULL,
		  `ativo` set(\'S\',\'N\') NOT NULL,
		  PRIMARY KEY (`id`)
		)';
		$this->db->query($sql);
		$ret = $this->_add_registros();
		return $ret;
	}
	/**
	 * adiciona as paginas padrão
	 *
	 * @return integer
	 */
	private function _add_registros()
	{
